define('assessment',['api','chartMgr'], function(api, chart){
	
	var answers,
		duration = 0.6,
		questionCount = 7,
		questionWeight = Math.round(100 / questionCount * 1000) / 1000,
		// Global language variable
		locale = lang,
		answerBreakdown;
	
	return {
		init: init,
		recordAnswer: recordAnswer,
		storeAnswer: storeAnswer,
		getAnswers: getAnswers,
		complete: complete
	}

	
	function init() {
		answers = {
			'HWL': 0,
			'NWA': 0,
			'IOT': 0,
			'SEC': 0,
			'MOB': 0,
			'ANL': 0,
			'ANI': 0
		};
		api.fetchAnswerBreakdown(locale)
			.then(function(data){
				answerBreakdown = data;
			});
		$('[data-assessment-input]').on('click', recordAnswer);
	}
	
	
	function recordAnswer(ev){
		var delay = 0,
			input = ev.currentTarget,
			charted = $(ev.currentTarget).data('chartAnswer');
		
		if (charted) {
			chart.updateCustomerData(input.name, metadata.answerWeights[input.value]);
			answers[input.name] = input.value;

			var slide = $(input).parents('.question');
			var feedback = slide.find('.question-feedback');
			var copyNode = feedback.find('.commentary div');
			var percentNode = feedback.find('.popularity .value');
			var questionId = getQuestionId(slide);

			var copy = metadata.answerFeedback[input.value].q;

			if (feedback.css('opacity') === '1') {
				delay = duration + 0.2;
				TweenMax.to(copyNode, duration, {
					opacity: 0, 
					ease: Expo.easeInOut,
					onComplete: setFeedbackCopy,
					onCompleteParams:[copyNode, copy]
				});
			} else {
				setFeedbackCopy(copyNode, copy);
			}

			var allAnswers = [];
			var thisAnswer = 0;
			var totalResponses = 1;
			if (answerBreakdown[questionId]) {
				allAnswers = answerBreakdown[questionId];
			};
			if (allAnswers[input.value]) {
				totalResponses = 0;
				for (aid in allAnswers) {
					if (aid) {
						totalResponses += allAnswers[aid];
					}
				}
				thisAnswer = allAnswers[input.value];
			}
			
			var current = parseInt(percentNode.text());
			var percent = Math.round(thisAnswer / totalResponses * 100);
			TweenMax.to(feedback, duration, {
				opacity: 1, 
				delay: delay,
				ease: Expo.easeInOut
			});
			updateScore(percentNode, current, percent);
		}
	}
	
	
	function updateScore(node, old, latest) {
		var diff = latest - old;
		var data = {
			percent: old
		};
		TweenMax.to(data, 1, {
			percent:"+=" + diff,
			roundProps:"percent",
			onUpdate:updateDom,
			onUpdateParams: [node, data],
			ease:Power1.easeInOut
		});
	}


	function updateDom(node, animatedData) {
		node.html(animatedData.percent);
	}

	
	function setFeedbackCopy(node, copy) {
		node.html(copy).css('opacity', 1);
	}

	function storeAnswer(currentStep) {
		if (!currentStep || currentStep === 1) {
			return;
		}
		var answers = $('.step-' + currentStep + ' input:checked');
		var questionId = getQuestionId($('.step-' + currentStep));
		var responses = [];
		answers.each(function(idx, node){
			var r = {
				a: node.value,
				q: questionId,
				w: questionWeight
			};
			responses.push(r);
		});
		//  Question 11 and 12 are on the same slide so we only pick up the ID for 11.
		//  The second answer should be for question 12.
		if (questionId == 11) {
			responses[1].q = 12;
		}
		api.storeAnswers(responses, locale);
	}
	
	
	function complete() {
		var finalScore = 0;
		for( var key in  answers) {
			var val = answers[key];
			finalScore += (100 / questionCount) * (metadata.answerWeights[val] / 100);
		}
		finalScore = Math.round(finalScore);

		api.storeScore(finalScore, locale);
		
		return finalScore;
	}

	function getAnswers() {
		return answers;
	}

	function getQuestionId(slide) {
		var classes = slide.attr('class').split(/\s+/);
		var qId = 0;
		var regex = /question-(\d+)/;
		classes.forEach(function(className){
			var matches = className.match(regex);
			if (matches) {
				qId = matches[0].split('-').pop();
			}
		});

		return qId;
	}
});

